import React, {Component} from 'react';
import Form from 'react-bootstrap/Form'

class RegistrationAlert extends Component{

    constructor(props){
        super(props);

        this.state = {
            visable: this.props.visable,
            variant: this.props.variant,
            heading: this.props.heading,
            message: this.props.message,
        }
    }

    setMessage = (message) => {
        this,this.setState({message: message});
    }
    setHeading = (heading) => {
        this,this.setState({heading: heading});
    }
    setVariant = (variant) => {
        this,this.setState({variant : variant});
    }
    setVisable = (visable) => {
        this,this.setState({visable : visable});
    }

}

export default RegistrationAlert
